import React from "react";
import { Link } from "react-router-dom"; 
import ProductDetails from "./ProductDetails";

function ProductCard({ product }) {
  return (
    <Link to={`/product/${product.id}`}> {ProductDetails}
      <div className="bg-white p-4 border border-gray-200 rounded-lg m-auto shadow-lg">
        <img
          src={product.thumbnail}
          alt={product.title}
          className="w-60 h-80 object-cover mb-2"
        />
        <h3 className="font-semibold text-lg">{product.title}</h3>
        <div className="container w-60 h-10 border border-gray-300 overflow-hidden">
          <p className="first-description text-sm text-gray-700 p-2">
            {product.description}
          </p>
        </div>
        <p className="text-blue-500 font-semibold">${product.price}</p>
        <p>Rating: {product.rating}</p>
        <p>Brand: {product.brand}</p>
      </div>
    </Link>
  );
}

export default ProductCard;
