import React from "react";

function ProductCard({ product }) {
  return (

      <div className="bg-white p-4 border border-gray-200 rounded-lg m-auto">
        <img
          src={product.thumbnail}
          alt={product.title}
          className="w-60 h-55 object-cover mb-2"
        />
        <h3 className="font-semibold text-lg">{product.title}</h3>
        <div className="container w-60 h-10 border border-gray-300 overflow-hidden"><p className="first-description text-sm text-gray-700 p-2:">{product.description}</p></div>
        <p className="text-blue-500 font-semibold">${product.price}</p>
        <p>Rating: {product.rating}</p>
        <p>Brand: {product.brand}</p>
        {/* <button onClick={() => viewDetails(product.id)} className="mt-2 bg-blue-500 text-white px-4 py-2 rounded-lg">View Details</button> */}
      </div>

  );
}
export default ProductCard;
