import React, { useState, useEffect } from 'react';

const Card = () => {
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const [productSize, setProductSize] = useState(3);

  useEffect(() => {
    const fetchProducts = async () => {
      const response = await fetch(`https://dummyjson.com/products`);
      const data = await response.json();
      setProducts([...data.products]);
    };

    fetchProducts();
  }, []);

  const renderProducts = () => {
    const productGrid = document.getElementById('productGrid');
    productGrid.innerHTML = '';

    const startIndex = currentPage;
    const endIndex = productSize;

    products.slice(startIndex, endIndex).forEach(product => {
      const card = (
        <div key={product.id} className="bg-white p-4 border border-gray-200 rounded-lg">
          <img src={product.thumbnail} alt={product.title} className="w-full h-40 object-cover mb-2" />
          <h3 className="font-semibold text-lg">{product.title}</h3>
          <p className="text-gray-600">{product.description}</p>
          <p className="text-blue-500 font-semibold">${product.price}</p>
          <p>Rating: {product.rating}</p>
          <p>Brand: {product.brand}</p>
          <button onClick={() => viewDetails(product.id)} className="mt-2 bg-blue-500 text-white px-4 py-2 rounded-lg">View Details</button>
        </div>
      );
      productGrid.appendChild(card);
    });
  };

  const isBottomOfPage = () => {
    return (window.innerHeight + window.scrollY) >= document.body.offsetHeight;
  };

  const loadMoreProducts = () => {
    setProductSize(products.length);
    renderProducts();
  };

  window.addEventListener('scroll', () => {
    if (isBottomOfPage()) {
      loadMoreProducts();
    }
  });

  const searchProducts = () => {
    const searchInput = document.getElementById('searchInput').value.toLowerCase();
    const filteredProducts = products.filter(product => product.title.toLowerCase().includes(searchInput));
    const productGrid = document.getElementById('productGrid');
    productGrid.innerHTML = '';

    filteredProducts.forEach(product => {
      const card = (
        <div key={product.id} className="bg-white p-4 border border-gray-200 rounded-lg">
          <img src={product.thumbnail} alt={product.title} className="w-full h-40 object-cover mb-2" />
          <h3 className="font-semibold text-lg">{product.title}</h3>
          <p className="text-gray-600">{product.description}</p>
          <p className="text-blue-500 font-semibold">${product.price}</p>
          <button onClick={() => viewDetails(product.id)} className="mt-2 bg-blue-500 text-white px-4 py-2 rounded-lg">View Details</button>
        </div>
      );
      productGrid.appendChild(card);
    });
  };

  const viewDetails = (productId) => {
    const product = products.find(prod => prod.id === productId);
    localStorage.setItem('selectedProduct', JSON.stringify(product));
    window.location.href = 'product-details.html';
  };

  return (
    <div>
      <div className="mb-4 flex justify-center">
        <input id="searchInput" type="text" placeholder="Search your favorite SmartPhone" className="p-2 border border-gray-300 rounded-lg w-8/12" />
        <button onClick={searchProducts} className="bg-blue-500 text-white px-4 py-2 rounded-lg ml-2">Search</button>
      </div>

      <div id="productGrid" className="grid grid-cols-1 md:grid-cols-3 gap-4"></div>
    </div>
  );
};

export default Card;
