import React, { useState, useEffect } from 'react';

function Products() {
  const [products, setProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const [productSize] = useState(3);
  const [searchInput, setSearchInput] = useState('');

  useEffect(() => {
    fetchProducts();
  }, []);

  async function fetchProducts() {
    const response = await fetch(`https://dummyjson.com/products`);
    const data = await response.json();
    setProducts(data.products);
  }

  function renderProducts() {
    const startIndex = currentPage;
    const endIndex = currentPage + productSize;
    return products
      .filter(product => product.title.toLowerCase().includes(searchInput.toLowerCase()))
      .slice(startIndex, endIndex)
      .map(product => (
        <div key={product.id} className="bg-white p-4 border border-gray-200 rounded-lg">
          <img src={product.thumbnail} alt={product.title} className="w-full h-40 object-cover mb-2" />
          <h3 className="font-semibold text-lg">{product.title}</h3>
          <p className="text-gray-600">{product.description}</p>
          <p className="text-blue-500 font-semibold">${product.price}</p>
          <button onClick={() => viewDetails(product.id)} className="mt-2 bg-blue-500 text-white px-4 py-2 rounded-lg">View Details</button>
        </div>
      ));
  }

  function isBottomOfPage() {
    return (window.innerHeight + window.scrollY) >= document.body.offsetHeight;
  }

  function loadMoreProducts() {
    setCurrentPage(currentPage + productSize);
  }

  useEffect(() => {
    function handleScroll() {
      if (isBottomOfPage()) {
        loadMoreProducts();
      }
    }

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [currentPage]);

  function viewDetails(productId) {
    localStorage.setItem('selectedProduct', JSON.stringify(products.find(prod => prod.id === productId)));
    window.location.href = 'product-details.html';
  }

  const handleInputChange = (event) => {
    setSearchInput(event.target.value);
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Search your favorite SmartPhone"
        className="p-2 border border-gray-300 rounded-lg w-8/12"
        value={searchInput}
        onChange={handleInputChange}
      />
      <div id="productGrid" className="flex flex-wrap justify-between">
        {renderProducts()}
      </div>
    </div>
  );
}

export default Products;
