import React, { useState, useEffect } from "react";

function ProductCard({ product }) {
  return (
    <div>
      <div className="mb-4 flex justify-center">
        <input id="searchInput" type="text" placeholder="Search your favorite SmartPhone"
                className="p-2 border border-gray-300 rounded-lg w-8/12"/>
        <button
          onclick="searchProducts()"
          className="bg-blue-500 text-white px-4 py-2 rounded-lg ml-2"
        >
          Search
        </button>
      </div>

      <div id="productGrid" class="grid grid-cols-1 md:grid-cols-3 gap-4"></div>
      <div className="bg-white p-4 border border-gray-200 rounded-lg">
        <img
          src={product.thumbnail}
          alt={product.title}
          className="w-full h-40 object-cover mb-2"
        />
        <h3 className="font-semibold text-lg">{product.title}</h3>
        <p className="text-gray-600">{product.description}</p>
        <p className="text-blue-500 font-semibold">${product.price}</p>
        <p>Rating: {product.rating}</p>
        <p>Brand: {product.brand}</p>
        {/* <button onClick={() => viewDetails(product.id)} className="mt-2 bg-blue-500 text-white px-4 py-2 rounded-lg">View Details</button> */}
      </div>
    </div>
  );
}
export default ProductCard;
